import React, { useState, useEffect } from 'react';
import Chat from './components/Chat';
import ReflectionView from './components/Reflection/ReflectionView';
import PersonaView from './components/Persona/PersonaView';
import ErrorBoundary from './components/ErrorBoundary';
import NavSidebar from './components/Layout/NavSidebar';
import DiagnosticsBanner from './components/System/DiagnosticsBanner';
import LicenseStatus from './components/LicenseStatus';
import LicenseExpiryModal from './components/LicenseExpiryModal';
import { getOrCreateSessionId } from './services/sessionService';

function App() {
  const [activeTab, setActiveTab] = useState('chat');
  const [userId, setUserId] = useState('');
  const [licenseExpired, setLicenseExpired] = useState(false);
  const [expiryMessage, setExpiryMessage] = useState('');
  const [expiredAtRuntime, setExpiredAtRuntime] = useState(false);

  // Generate/retrieve a stable session/user ID via centralized service
  useEffect(() => {
    const id = getOrCreateSessionId();
    setUserId(id);
  }, []);

  // Listen for license expiry events
  useEffect(() => {
    const handleLicenseExpiry = (event) => {
      console.log('License expired event received:', event.detail);
      setExpiryMessage(event.detail.message || 'Your 30-day beta trial has expired.');
      setExpiredAtRuntime(event.detail.expired_at_runtime || false);
      setLicenseExpired(true);
    };

    window.addEventListener('licenseExpired', handleLicenseExpiry);

    return () => {
      window.removeEventListener('licenseExpired', handleLicenseExpiry);
    };
  }, []);

  const handleCloseExpiryModal = () => {
    setLicenseExpired(false);

    // If expired at runtime, we'll reload the page anyway
    if (!expiredAtRuntime) {
      // For startup expiry, we might want to close the app or show a different state
      console.log('License expired - application should be closed');
    }
  };

  return (
    <ErrorBoundary>
      <div className="min-h-screen bg-[var(--color-bg)] text-[var(--color-text-primary)] flex">
        {/* Sidebar */}
        <NavSidebar activeTab={activeTab} onChange={setActiveTab} />

        {/* Main content area */}
        <div className="flex-1 flex flex-col">
          {/* Minimal header */}
          <header className="h-16 border-b border-[var(--color-border)] flex items-center justify-between px-4">
            <div className="flex flex-col leading-tight">
              <div className="text-base font-semibold text-[var(--color-text-primary)]">SELO DSP</div>
            </div>
            <div className="flex items-center gap-4">
              <LicenseStatus />
              <div className="text-xs text-[var(--color-text-muted)]">{new Date().getFullYear()}</div>
            </div>
          </header>

          {/* Diagnostics banner (GPU/CPU status) */}
          <DiagnosticsBanner />

          {/* Content */}
          <main className="flex-1 p-4 overflow-y-auto">
            <div className="max-w-6xl mx-auto">
              <ErrorBoundary>
                {activeTab === 'chat' ? (
                  <Chat userId={userId} />
                ) : activeTab === 'reflection' ? (
                  <ReflectionView userId={userId} />
                ) : (
                  <PersonaView userId={userId} />
                )}
              </ErrorBoundary>
            </div>
          </main>
        </div>
      </div>

      {/* License Expiry Modal */}
      <LicenseExpiryModal
        isOpen={licenseExpired}
        onClose={handleCloseExpiryModal}
        message={expiryMessage}
        expiredAtRuntime={expiredAtRuntime}
      />
    </ErrorBoundary>
  );
}

export default App;
