import React, { useState, useEffect } from 'react';
import './LicenseExpiryModal.css';

const LicenseExpiryModal = ({ isOpen, onClose, message, expiredAtRuntime = false }) => {
  const [countdown, setCountdown] = useState(10);

  useEffect(() => {
    if (!isOpen) return;

    // Start countdown for auto-close
    const timer = setInterval(() => {
      setCountdown(prev => {
        if (prev <= 1) {
          clearInterval(timer);
          // Auto-close and potentially reload page
          handleClose();
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [isOpen]);

  const handleClose = () => {
    onClose();

    // If license expired during runtime, reload the page to prevent further usage
    if (expiredAtRuntime) {
      setTimeout(() => {
        window.location.reload();
      }, 1000);
    }
  };

  const handleReload = () => {
    window.location.reload();
  };

  if (!isOpen) return null;

  return (
    <div className="license-expiry-overlay">
      <div className="license-expiry-modal">
        <div className="license-expiry-header">
          <div className="license-expiry-icon">ðŸš¨</div>
          <h2>Beta Trial Expired</h2>
        </div>

        <div className="license-expiry-content">
          <p className="license-expiry-message">
            {message || 'Your 30-day beta trial has expired.'}
          </p>

          {expiredAtRuntime && (
            <div className="license-expiry-runtime-notice">
              <p>
                <strong>License expired during this session.</strong><br/>
                The application will be reloaded to prevent further usage.
              </p>
            </div>
          )}

          <div className="license-expiry-details">
            <h3>Thank you for testing SELO DSP!</h3>
            <p>
              We hope you enjoyed exploring SELO DSP's capabilities during your 30-day trial period.
              Your feedback is valuable to us as we continue developing this technology.
            </p>

            <div className="license-expiry-next-steps">
              <h4>What's next?</h4>
              <ul>
                <li>Contact us for information about the full version</li>
                <li>Share your feedback about the beta experience</li>
                <li>Stay tuned for the official release announcement</li>
              </ul>
            </div>
          </div>
        </div>

        <div className="license-expiry-actions">
          {expiredAtRuntime ? (
            <div className="license-expiry-countdown">
              <button 
                onClick={handleReload}
                className="license-expiry-button primary"
              >
                Reload Now
              </button>
              <p className="license-expiry-countdown-text">
                Auto-reloading in {countdown} seconds...
              </p>
            </div>
          ) : (
            <button 
              onClick={handleClose}
              className="license-expiry-button primary"
            >
              Close Application
            </button>
          )}
        </div>

        <div className="license-expiry-footer">
          <p>
            <strong>SELO DSP Beta</strong> - Thank you for your participation
          </p>
        </div>
      </div>
    </div>
  );
};

export default LicenseExpiryModal;
